INSERT INTO `appointments` VALUES ('c3b188d3-872d-4647-afc2-343341f9e5a7','Р”РёСЂРµРєС‚РѕСЂ',_binary '\Д\ир\е\кт\ор \пр\е\д\пр\ият\ия \от\н\ос\ится \к \к\ат\е\г\ор\и\и ру\к\о\в\о\д\ит\е\л\е\й.\r\n\r\n\Н\а \д\о\л\ж\н\ость \д\ир\е\кт\ор\а \пр\е\д\пр\ият\ия \н\а\з\н\ач\а\ется \л\иц\о, \и\м\еющ\е\е \высш\е\е \пр\оф\есс\и\о\н\а\ль\н\о\е (т\ех\н\ич\ес\к\о\е \и\л\и \и\н\ж\е\н\ер\н\о-э\к\о\н\о\м\ич\ес\к\о\е) \о\бр\а\з\о\в\а\н\и\е \и ст\а\ж р\а\б\оты \н\а ру\к\о\в\о\дящ\их \д\о\л\ж\н\остях \в с\о\от\в\ет','20000.00','0',_binary '1. \Д\ир\е\кт\ор \пр\е\д\пр\ият\ия \от\н\ос\ится \к \к\ат\е\г\ор\и\и ру\к\о\в\о\д\ит\е\л\е\й.\r\n\r\n2. \Н\а \д\о\л\ж\н\ость \д\ир\е\кт\ор\а \пр\е\д\пр\ият\ия \н\а\з\н\ач\а\ется \л\иц\о, \и\м\еющ\е\е \высш\е\е \пр\оф\есс\и\о\н\а\ль\н\о\е (т\ех\н\ич\ес\к\о\е \и\л\и \и\н\ж\е\н\ер\н\о-э\к\о\н\о\м\ич\ес\к\о\е) \о\бр\а\з\о\в\а\н\и\е \и ст\а\ж р\а\б\оты \н\а ру\к\о\в\о\дящ\их \д\о\л\ж\н\остях \в с\о\от\в\етст\вующ\е\й \пр\оф\и\лю \пр\е\д\пр\ият\ия \отр\ас\л\и \н\е \м\е\н\е\е 5 \л\ет.\r\n\r\n3. \Н\а\з\н\ач\е\н\и\е \н\а \д\о\л\ж\н\ость \д\ир\е\кт\ор\а \пр\е\д\пр\ият\ия \и \ос\в\о\б\о\ж\д\е\н\и\е \от \н\е\е \пр\о\и\з\в\о\д\ится р\еш\е\н\и\е\м \о\бщ\е\г\о с\о\бр\а\н\ия учр\е\д\ит\е\л\е\й (\и\н\о\г\о \ор\г\а\н\а \пр\е\д\пр\ият\ия).\r\n\r\n4. \Д\ир\е\кт\ор \пр\е\д\пр\ият\ия \д\о\л\ж\е\н \з\н\ать:\r\n\r\n4.1. \З\а\к\о\н\о\д\ат\е\ль\ны\е \и \н\ор\м\ат\и\в\ны\е \пр\а\в\о\вы\е \а\кты, р\е\г\л\а\м\е\нт\ирующ\и\е \пр\о\и\з\в\о\дст\в\е\н\н\о-х\о\зя\йст\в\е\н\ную \и ф\и\н\а\нс\о\в\о-э\к\о\н\о\м\ич\ес\кую \д\еят\е\ль\н\ость \пр\е\д\пр\ият\ия, \п\ост\а\н\о\в\л\е\н\ия ф\е\д\ер\а\ль\ных, р\е\г\и\о\н\а\ль\ных \и \м\ест\ных \ор\г\а\н\о\в \г\осу\д\арст\в\е\н\н\о\й \в\л\аст\и \и у\пр\а\в\л\е\н\ия, \о\пр\е\д\е\ляющ\и\е \пр\и\ор\ит\ет\ны\е \н\а\пр\а\в\л\е\н\ия р\а\з\в\ит\ия э\к\о\н\о\м\и\к\и \и с\о\от\в\етст\вующ\е\й \отр\ас\л\и.\r\n\r\n4.2. \М\ет\о\д\ич\ес\к\и\е \и \н\ор\м\ат\и\в\ны\е \м\ат\ер\и\а\лы \дру\г\их \ор\г\а\н\о\в, \к\ас\ающ\и\еся \д\еят\е\ль\н\ост\и \пр\е\д\пр\ият\ия.\r\n\r\n4.3. \Пр\оф\и\ль, с\п\ец\и\а\л\и\з\ац\ию \и \ос\о\б\е\н\н\ост\и стру\ктуры \пр\е\д\пр\ият\ия.\r\n\r\n4.4. \П\ерс\п\е\кт\и\вы т\ех\н\ич\ес\к\о\г\о, э\к\о\н\о\м\ич\ес\к\о\г\о \и с\оц\и\а\ль\н\о\г\о р\а\з\в\ит\ия \отр\ас\л\и \и \пр\е\д\пр\ият\ия.\r\n\r\n4.5. \Пр\о\и\з\в\о\дст\в\е\н\ны\е \м\ощ\н\ост\и \и \к\а\др\о\вы\е р\есурсы \пр\е\д\пр\ият\ия.\r\n\r\n4.6. \Т\ех\н\о\л\о\г\ию \пр\о\и\з\в\о\дст\в\а \пр\о\ду\кц\и\и \пр\е\д\пр\ият\ия.\r\n\r\n4.7. \Н\а\л\о\г\о\в\о\е \и э\к\о\л\о\г\ич\ес\к\о\е \з\а\к\о\н\о\д\ат\е\льст\в\о.\r\n\r\n4.8. \П\оря\д\о\к с\ост\а\в\л\е\н\ия \и с\о\г\л\ас\о\в\а\н\ия \б\и\з\н\ес-\п\л\а\н\о\в \пр\о\и\з\в\о\дст\в\е\н\н\о-х\о\зя\йст\в\е\н\н\о\й \и ф\и\н\а\нс\о\в\о-э\к\о\н\о\м\ич\ес\к\о\й \д\еят\е\ль\н\ост\и \пр\е\д\пр\ият\ия.\r\n\r\n4.9. \Ры\н\оч\ны\е \м\ет\о\ды х\о\зя\йст\в\о\в\а\н\ия \и у\пр\а\в\л\е\н\ия \пр\е\д\пр\ият\и\е\м.\r\n\r\n4.10. \С\ист\е\му э\к\о\н\о\м\ич\ес\к\их \и\н\д\и\к\ат\ор\о\в, \п\о\з\в\о\ляющ\их \пр\е\д\пр\ият\ию \о\пр\е\д\е\лять с\в\о\е \п\о\л\о\ж\е\н\и\е \н\а ры\н\к\е \и р\а\зр\а\б\аты\в\ать \пр\о\гр\а\м\мы \вых\о\д\а \н\а \н\о\вы\е ры\н\к\и с\быт\а.\r\n\r\n4.11. \П\оря\д\о\к \з\а\к\люч\е\н\ия \и \ис\п\о\л\н\е\н\ия х\о\зя\йст\в\е\н\ных \и ф\и\н\а\нс\о\вых \д\о\г\о\в\ор\о\в.\r\n\r\n4.12. \К\о\нъю\н\ктуру ры\н\к\а.\r\n\r\n4.13. \Н\ауч\н\о-т\ех\н\ич\ес\к\и\е \д\ост\и\ж\е\н\ия \и \п\ер\е\д\о\в\о\й \о\пыт \в с\о\от\в\етст\вующ\е\й \отр\ас\л\и \пр\о\и\з\в\о\дст\в\а.\r\n\r\n4.14. \У\пр\а\в\л\е\н\и\е э\к\о\н\о\м\и\к\о\й \и ф\и\н\а\нс\а\м\и \пр\е\д\пр\ият\ия.\r\n\r\n4.15. \Ор\г\а\н\и\з\ац\ию \пр\о\и\з\в\о\дст\в\а \и тру\д\а.\r\n\r\n4.16. \П\оря\д\о\к р\а\зр\а\б\от\к\и \и \з\а\к\люч\е\н\ия \отр\ас\л\е\вых т\ар\иф\ных с\о\г\л\аш\е\н\и\й, \к\о\л\л\е\кт\и\в\ных \д\о\г\о\в\ор\о\в \и р\е\гу\л\ир\о\в\а\н\ия с\оц\и\а\ль\н\о-тру\д\о\вых \от\н\ош\е\н\и\й.\r\n\r\n4.17. \Тру\д\о\в\о\е \з\а\к\о\н\о\д\ат\е\льст\в\о.\r\n\r\n4.18. \Пр\а\в\и\л\а \и \н\ор\мы \охр\а\ны тру\д\а.\r\n\r\n5. \Д\ир\е\кт\ор \пр\е\д\пр\ият\ия \п\о\д\отч\ет\е\н учр\е\д\ит\е\ля\м \пр\е\д\пр\ият\ия \в \л\иц\е (\о\бщ\е\г\о с\о\бр\а\н\ия \а\кц\и\о\н\ер\о\в; \и\н\о\г\о \ор\г\а\н\а).\r\n\r\n6. \Н\а \вр\е\мя \отсутст\в\ия \д\ир\е\кт\ор\а \пр\е\д\пр\ият\ия \е\г\о \д\о\л\ж\н\ост\ны\е \о\бя\з\а\н\н\ост\и \вы\п\о\л\ня\ет (ют) \з\а\м\ест\ит\е\ль(\и), \н\а\з\н\ач\а\е\мы\й \в уст\а\н\о\в\л\е\н\н\о\м \п\оря\д\к\е, \к\от\оры\й(\е) \н\ес\ет(ут) \от\в\етст\в\е\н\н\ость \з\а \к\ач\ест\в\е\н\н\о\е, эфф\е\кт\и\в\н\о\е \и с\в\о\е\вр\е\м\е\н\н\о\е \их \вы\п\о\л\н\е\н\и\е.\r\nII. \Д\о\л\ж\н\ост\ны\е \о\бя\з\а\н\н\ост\и \r\n\r\n\Д\ир\е\кт\ор \пр\е\д\пр\ият\ия:\r\n\r\n1. \Ру\к\о\в\о\д\ит \в с\о\от\в\етст\в\и\и с \д\е\йст\вующ\и\м \з\а\к\о\н\о\д\ат\е\льст\в\о\м \пр\о\и\з\в\о\дст\в\е\н\н\о-х\о\зя\йст\в\е\н\н\о\й \и ф\и\н\а\нс\о\в\о-э\к\о\н\о\м\ич\ес\к\о\й \д\еят\е\ль\н\остью \пр\е\д\пр\ият\ия, \н\еся \всю \п\о\л\н\оту \от\в\етст\в\е\н\н\ост\и \з\а \п\ос\л\е\дст\в\ия \пр\и\н\и\м\а\е\мых р\еш\е\н\и\й, с\охр\а\н\н\ость \и эфф\е\кт\и\в\н\о\е \ис\п\о\ль\з\о\в\а\н\и\е \и\мущ\ест\в\а \пр\е\д\пр\ият\ия, \а т\а\к\ж\е ф\и\н\а\нс\о\в\о-х\о\зя\йст\в\е\н\ны\е р\е\зу\льт\аты \е\г\о \д\еят\е\ль\н\ост\и.\r\n\r\n2. \Ор\г\а\н\и\зу\ет р\а\б\оту \и эфф\е\кт\и\в\н\о\е \в\з\а\и\м\о\д\е\йст\в\и\е \вс\ех стру\ктур\ных \п\о\др\а\з\д\е\л\е\н\и\й, ц\ех\о\в \и \пр\о\и\з\в\о\дст\в\е\н\ных \е\д\и\н\иц, \н\а\пр\а\в\ля\ет \их \д\еят\е\ль\н\ость \н\а р\а\з\в\ит\и\е \и с\о\в\ерш\е\нст\в\о\в\а\н\и\е \пр\о\и\з\в\о\дст\в\а с уч\ет\о\м с\оц\и\а\ль\ных \и ры\н\оч\ных \пр\и\ор\ит\ет\о\в, \п\о\выш\е\н\и\е эфф\е\кт\и\в\н\ост\и р\а\б\оты \пр\е\д\пр\ият\ия, р\ост \о\бъ\е\м\о\в с\быт\а \пр\о\ду\кц\и\и \и у\в\е\л\ич\е\н\и\е \пр\и\бы\л\и, \к\ач\ест\в\а \и \к\о\н\кур\е\нт\ос\п\ос\о\б\н\ост\и \пр\о\и\з\в\о\д\и\м\о\й \пр\о\ду\кц\и\и, \е\е с\о\от\в\етст\в\и\е \м\ир\о\вы\м ст\а\н\д\арт\а\м \в ц\е\лях \з\а\в\о\е\в\а\н\ия \от\еч\ест\в\е\н\н\о\г\о \и \з\ару\б\е\ж\н\о\г\о ры\н\к\а \и у\д\о\в\л\ет\в\ор\е\н\ия \п\отр\е\б\н\ост\е\й \н\ас\е\л\е\н\ия \в с\о\от\в\етст\вующ\их \в\и\д\ах \от\еч\ест\в\е\н\н\о\й \пр\о\ду\кц\и\и.\r\n\r\n3. \О\б\ес\п\еч\и\в\а\ет \вы\п\о\л\н\е\н\и\е \пр\е\д\пр\ият\и\е\м \вс\ех \о\бя\з\ат\е\льст\в\а \п\ер\е\д ф\е\д\ер\а\ль\ны\м, р\е\г\и\о\н\а\ль\ны\м \и \м\ест\ны\м \бю\д\ж\ет\а\м\и, \г\осу\д\арст\в\е\н\ны\м\и \в\н\е\бю\д\ж\ет\ны\м\и с\оц\и\а\ль\ны\м\и ф\о\н\д\а\м\и, \п\ост\а\вщ\и\к\а\м\и, \з\а\к\а\зч\и\к\а\м\и \и \кр\е\д\ит\ор\а\м\и, \в\к\люч\ая учр\е\ж\д\е\н\ия \б\а\н\к\а, \а т\а\к\ж\е х\о\зя\йст\в\е\н\ных \и тру\д\о\вых \д\о\г\о\в\ор\о\в (\к\о\нтр\а\кт\о\в \и \б\и\з\н\ес-\п\л\а\н\о\в).\r\n\r\n4. \Ор\г\а\н\и\зу\ет \пр\о\и\з\в\о\дст\в\е\н\н\о-х\о\зя\йст\в\е\н\ную \д\еят\е\ль\н\ость \н\а \ос\н\о\в\е ш\ир\о\к\о\г\о \ис\п\о\ль\з\о\в\а\н\ия \н\о\в\е\йш\е\й т\ех\н\и\к\и \и т\ех\н\о\л\о\г\и\и, \пр\о\гр\есс\и\в\ных ф\ор\м у\пр\а\в\л\е\н\ия \и \ор\г\а\н\и\з\ац\и\и тру\д\а, \н\ауч\н\о-\о\б\ос\н\о\в\а\н\ных \н\ор\м\ат\и\в\о\в \м\ат\ер\и\а\ль\ных, ф\и\н\а\нс\о\вых \и тру\д\о\вых \з\атр\ат, \и\зуч\е\н\ия \к\о\нъю\н\ктуры ры\н\к\а \и \п\ер\е\д\о\в\о\г\о \о\пыт\а (\от\еч\ест\в\е\н\н\о\г\о \и \з\ару\б\е\ж\н\о\г\о) \в ц\е\лях \вс\е\м\ер\н\о\г\о \п\о\выш\е\н\ия т\ех\н\ич\ес\к\о\г\о ур\о\в\ня \и \к\ач\ест\в\а \пр\о\ду\кц\и\и (ус\лу\г), э\к\о\н\о\м\ич\ес\к\о\й эфф\е\кт\и\в\н\ост\и \е\е \пр\о\и\з\в\о\дст\в\а, р\ац\и\о\н\а\ль\н\о\г\о \ис\п\о\ль\з\о\в\а\н\ия \пр\о\и\з\в\о\дст\в\е\н\ных р\е\з\ер\в\о\в \и э\к\о\н\о\м\н\о\г\о р\асх\о\д\о\в\а\н\ия \вс\ех \в\и\д\о\в р\есурс\о\в.\r\n\r\n5. \Пр\и\н\и\м\а\ет \м\еры \п\о \о\б\ес\п\еч\е\н\ию \пр\е\д\пр\ият\ия \к\в\а\л\иф\иц\ир\о\в\а\н\ны\м\и \к\а\др\а\м\и, р\ац\и\о\н\а\ль\н\о\му \ис\п\о\ль\з\о\в\а\н\ию \и р\а\з\в\ит\ию \их \пр\оф\есс\и\о\н\а\ль\ных \з\н\а\н\и\й \и \о\пыт\а, с\о\з\д\а\н\ию \б\е\з\о\п\ас\ных \и \б\л\а\г\о\пр\ият\ных \д\ля \ж\и\з\н\и \и \з\д\ор\о\вья ус\л\о\в\и\й тру\д\а, с\о\б\лю\д\е\н\ию тр\е\б\о\в\а\н\и\й \з\а\к\о\н\о\д\ат\е\льст\в\а \о\б \охр\а\н\е \о\кру\ж\ающ\е\й ср\е\ды.\r\n\r\n6. \О\б\ес\п\еч\и\в\а\ет \пр\а\в\и\ль\н\о\е с\оч\ет\а\н\и\е э\к\о\н\о\м\ич\ес\к\их \и \а\д\м\и\н\истр\ат\и\в\ных \м\ет\о\д\о\в ру\к\о\в\о\дст\в\а, \е\д\и\н\о\н\ач\а\л\ия \и \к\о\л\л\е\г\и\а\ль\н\ост\и \в \о\бсу\ж\д\е\н\и\и \и р\еш\е\н\и\и \в\о\пр\ос\о\в, \м\ат\ер\и\а\ль\ных \и \м\ат\ер\и\а\ль\ных ст\и\му\л\о\в \п\о\выш\е\н\ия эфф\е\кт\и\в\н\ост\и \пр\о\и\з\в\о\дст\в\а, \пр\и\м\е\н\е\н\и\е \пр\и\нц\и\п\а \м\ат\ер\и\а\ль\н\о\й \з\а\и\нт\ер\ес\о\в\а\н\н\ост\и \и \от\в\етст\в\е\н\н\ост\и \к\а\ж\д\о\г\о р\а\б\от\н\и\к\а \з\а \п\оруч\е\н\н\о\е \е\му \д\е\л\о \и р\е\зу\льт\аты р\а\б\оты \вс\е\г\о \к\о\л\л\е\кт\и\в\а, \вы\п\л\ату \з\ар\а\б\от\н\о\й \п\л\аты \в уст\а\н\о\в\л\е\н\ны\е ср\о\к\и.\r\n\r\n7. \С\о\в\м\ест\н\о с тру\д\о\вы\м\и \к\о\л\л\е\кт\и\в\а\м\и \и \пр\офс\ою\з\ны\м\и \ор\г\а\н\и\з\ац\ия\м\и \о\б\ес\п\еч\и\в\а\ет \н\а \ос\н\о\в\е \пр\и\нц\и\п\о\в с\оц\и\а\ль\н\о\г\о \п\арт\н\ерст\в\а р\а\зр\а\б\от\ку, \з\а\к\люч\е\н\и\е \и \вы\п\о\л\н\е\н\и\е \к\о\л\л\е\кт\и\в\н\о\г\о \д\о\г\о\в\ор\а, с\о\б\лю\д\е\н\и\е тру\д\о\в\о\й \д\исц\и\п\л\и\ны, с\п\ос\о\бст\ву\ет р\а\з\в\ит\ию тру\д\о\в\о\й \м\от\и\в\ац\и\и, \и\н\иц\и\ат\и\вы \и \а\кт\и\в\н\ост\и р\а\б\оч\их \и с\лу\ж\ащ\их \пр\е\д\пр\ият\ия.\r\n\r\n8. \Р\еш\а\ет \в\о\пр\осы, \к\ас\ающ\и\еся ф\и\н\а\нс\о\в\о-э\к\о\н\о\м\ич\ес\к\о\й \и \пр\о\и\з\в\о\дст\в\е\н\н\о-х\о\зя\йст\в\е\н\н\о\й \д\еят\е\ль\н\ост\и \пр\е\д\пр\ият\ия, \в \пр\е\д\е\л\ах \пр\е\д\ост\а\в\л\е\н\ных \е\му \з\а\к\о\н\о\д\ат\е\льст\в\о\м \пр\а\в, \п\оруч\а\ет \в\е\д\е\н\и\е \от\д\е\ль\ных \н\а\пр\а\в\л\е\н\и\й \д\еят\е\ль\н\ост\и \дру\г\и\м \д\о\л\ж\н\ост\ны\м \л\иц\а\м – \з\а\м\ест\ит\е\ля\м \д\ир\е\кт\ор\а, ру\к\о\в\о\д\ит\е\ля\м \пр\о\и\з\в\о\дст\в\е\н\ных \е\д\и\н\иц \и ф\и\л\и\а\л\о\в \пр\е\д\пр\ият\и\й, \а т\а\к\ж\е фу\н\кц\и\о\н\а\ль\ных \и \пр\о\и\з\в\о\дст\в\е\н\ных \п\о\др\а\з\д\е\л\е\н\и\й.\r\n\r\n9. \О\б\ес\п\еч\и\в\а\ет с\о\б\лю\д\е\н\и\е \з\а\к\о\н\н\ост\и \в \д\еят\е\ль\н\ост\и \пр\е\д\пр\ият\ия \и \осущ\ест\в\л\е\н\и\и \е\г\о х\о\зя\йст\в\е\н\н\о э\к\о\н\о\м\ич\ес\к\их с\вя\з\е\й, \ис\п\о\ль\з\о\в\а\н\и\е \пр\а\в\о\вых ср\е\дст\в \д\ля ф\и\н\а\нс\о\в\о\г\о у\пр\а\в\л\е\н\ия \и фу\н\кц\и\о\н\ир\о\в\а\н\ия \в ры\н\оч\ных ус\л\о\в\иях, у\кр\е\п\л\е\н\ия \д\о\г\о\в\ор\н\о\й \и ф\и\н\а\нс\о\в\о\й \д\исц\и\п\л\и\ны, р\е\гу\л\ир\о\в\а\н\ия с\оц\и\а\ль\н\о-тру\д\о\вых \от\н\ош\е\н\и\й, \о\б\ес\п\еч\е\н\ия \и\н\в\ест\иц\и\о\н\н\о\й \пр\и\в\л\е\к\ат\е\ль\н\ост\и \пр\е\д\пр\ият\ия \в ц\е\лях \п\о\д\д\ер\ж\а\н\ия \и р\асш\ир\е\н\ия \м\асшт\а\б\о\в \пр\е\д\пр\и\н\и\м\ат\е\льс\к\о\й \д\еят\е\ль\н\ост\и.\r\n\r\n10. \С\о\б\лю\д\а\ет тр\е\б\о\в\а\н\ия \охр\а\ны тру\д\а, уст\а\н\о\в\л\е\н\ны\е \з\а\к\о\н\а\м\и \и \и\ны\м\и \н\ор\м\ат\и\в\ны\м\и \пр\а\в\о\вы\м\и \а\кт\а\м\и, \а т\а\к\ж\е \пр\а\в\и\л\а\м\и \и \и\нстру\кц\ия\м\и \п\о \охр\а\н\е тру\д\а.\r\n\r\n11. \З\ащ\ищ\а\ет \и\мущ\ест\в\е\н\ны\е \и\нт\ер\есы \в су\д\е, \ар\б\итр\а\ж\е, \ор\г\а\н\о\в \г\осу\д\арст\в\е\н\н\о\й \в\л\аст\и \и у\пр\а\в\л\е\н\ия.\r\nIII. \Пр\а\в\а \r\n\r\n\Д\ир\е\кт\ор \пр\е\д\пр\ият\ия \и\м\е\ет \пр\а\в\о:\r\n\r\n1. Б\е\з \д\о\в\ер\е\н\н\ост\и \д\е\йст\в\о\в\ать \от \и\м\е\н\и \пр\е\д\пр\ият\ия.\r\n\r\n2. \Пр\е\дст\а\в\лять \и\нт\ер\есы \пр\е\д\пр\ият\ия \в\о \в\з\а\и\м\о\от\н\ош\е\н\иях с \гр\а\ж\д\а\н\а\м\и, юр\и\д\ич\ес\к\и\м\и \л\иц\а\м\и, \ор\г\а\н\а\м\и \г\осу\д\арст\в\е\н\н\о\й \в\л\аст\и \и у\пр\а\в\л\е\н\ия.\r\n\r\n3. \Р\ас\п\оря\ж\аться \и\мущ\ест\в\о\м \и ср\е\дст\в\а\м\и \пр\е\д\пр\ият\ия с с\о\б\лю\д\е\н\и\е\м тр\е\б\о\в\а\н\и\й, \о\пр\е\д\е\л\е\н\ных \з\а\к\о\н\о\д\ат\е\льст\в\о\м, \Уст\а\в\о\м \пр\е\д\пр\ият\ия, \и\ны\м\и \н\ор\м\ат\и\в\ны\м\и \пр\а\в\о\вы\м\и \а\кт\а\м\и.\r\n\r\n4. \От\кры\в\ать \в \б\а\н\к\о\вс\к\их учр\е\ж\д\е\н\иях р\асч\ет\ны\й \и \дру\г\и\е сч\ет\а.\r\n\r\n5. \З\а\к\люч\ать тру\д\о\вы\е \д\о\г\о\в\оры.\r\n\r\n6. \Пр\и\н\и\м\ать р\еш\е\н\ия \п\о \пр\е\дст\а\в\л\е\н\ия\м: \о \пр\и\в\л\еч\е\н\и\и р\а\б\от\н\и\к\о\в, \н\аруш\и\вш\их \пр\о\и\з\в\о\дст\в\е\н\ную \и тру\д\о\вую \д\исц\и\п\л\и\ну, \в\и\н\о\в\ных \в \пр\ич\и\н\е\н\и\и \м\ат\ер\и\а\ль\н\о\г\о ущ\ер\б\а \пр\е\д\пр\ият\ию \к \м\ат\ер\и\а\ль\н\о\й \и \д\исц\и\п\л\и\н\ар\н\о\й \от\в\етст\в\е\н\н\ост\и; \о \м\ор\а\ль\н\о\м \и \м\ат\ер\и\а\ль\н\о\м \п\о\ощр\е\н\и\и \ос\о\б\о \от\л\ич\и\вш\ихся р\а\б\от\н\и\к\о\в.\r\n\r\n7. \Вы\д\а\в\ать \д\о\в\ер\е\н\н\ост\и \н\а с\о\в\ерш\е\н\и\е \гр\а\ж\д\а\нс\к\о-\пр\а\в\о\вых с\д\е\л\о\к, \пр\е\дст\а\в\ит\е\льст\в\о, \пр.\r\n\r\n8. \В \пр\е\д\е\л\ах, уст\а\н\о\в\л\е\н\ных \з\а\к\о\н\о\д\ат\е\льст\в\о\м, \о\пр\е\д\е\лять с\ост\а\в \и \о\бъ\е\м с\в\е\д\е\н\и\й, с\ост\а\в\ляющ\их \к\о\м\м\ерч\ес\кую т\а\й\ну, \п\оря\д\о\к \е\е \з\ащ\иты.\r\nIV. \От\в\етст\в\е\н\н\ость \r\n\r\n1. \Д\ир\е\кт\ор \пр\е\д\пр\ият\ия \н\ес\ет \от\в\етст\в\е\н\н\ость:\r\n\r\n1.1. \З\а \н\е\н\а\д\л\е\ж\ащ\е\е \ис\п\о\л\н\е\н\и\е \и\л\и \н\е\ис\п\о\л\н\е\н\и\е с\в\о\их \д\о\л\ж\н\ост\ных \о\бя\з\а\н\н\ост\е\й, \пр\е\дус\м\отр\е\н\ных \н\аст\оящ\е\й \д\о\л\ж\н\ост\н\о\й \и\нстру\кц\и\е\й - \в \пр\е\д\е\л\ах, \о\пр\е\д\е\л\е\н\ных \д\е\йст\вующ\и\м тру\д\о\вы\м \з\а\к\о\н\о\д\ат\е\льст\в\о\м \Р\осс\и\йс\к\о\й \Ф\е\д\ер\ац\и\и.\r\n\r\n1.2. \З\а \пр\а\в\о\н\аруш\е\н\ия, с\о\в\ерш\е\н\ны\е \в \пр\оц\есс\е \осущ\ест\в\л\е\н\ия с\в\о\е\й \д\еят\е\ль\н\ост\и – \в \пр\е\д\е\л\ах, \о\пр\е\д\е\л\е\н\ных \д\е\йст\вующ\и\м \а\д\м\и\н\истр\ат\и\в\ны\м, у\г\о\л\о\в\ны\м \и \гр\а\ж\д\а\нс\к\и\м \з\а\к\о\н\о\д\ат\е\льст\в\о\м \Р\осс\и\йс\к\о\й \Ф\е\д\ер\ац\и\и.\r\n\r\n1.3. \З\а \пр\ич\и\н\е\н\и\е \м\ат\ер\и\а\ль\н\о\г\о ущ\ер\б\а – \в \пр\е\д\е\л\ах, \о\пр\е\д\е\л\е\н\ных \д\е\йст\вующ\и\м тру\д\о\вы\м \и \гр\а\ж\д\а\нс\к\и\м \з\а\к\о\н\о\д\ат\е\льст\в\о\м \Р\осс\и\йс\к\о\й \Ф\е\д\ер\ац\и\и.\r\n\r\n2. \Д\ир\е\кт\ор \пр\е\д\пр\ият\ия \н\ес\ет \п\ерс\о\н\а\ль\ную \от\в\етст\в\е\н\н\ость \з\а \п\ос\л\е\дст\в\ия \пр\и\нятых \и\м р\еш\е\н\и\й, \вых\о\дящ\их \з\а \пр\е\д\е\лы \е\г\о \п\о\л\н\о\м\оч\и\й, уст\а\н\о\в\л\е\н\ных \д\е\йст\вующ\и\м \з\а\к\о\н\о\д\ат\е\льст\в\о\м, \Уст\а\в\о\м \пр\е\д\пр\ият\ия, \и\ны\м\и \н\ор\м\ат\и\в\ны\м\и \пр\а\в\о\вы\м\и \а\кт\а\м\и. \Д\ир\е\кт\ор \пр\е\д\пр\ият\ия \н\е \ос\в\о\б\о\ж\д\а\ется \от \от\в\етст\в\е\н\н\ост\и, \ес\л\и \д\е\йст\в\ия, \в\л\е\кущ\и\е \от\в\етст\в\е\н\н\ость, \бы\л\и \пр\е\д\пр\и\няты \л\иц\а\м\и, \к\от\оры\м \о\н \д\е\л\е\г\ир\о\в\а\л с\в\о\и \пр\а\в\а.\r\n\r\n3. \Д\ир\е\кт\ор \пр\е\д\пр\ият\ия, \н\е\д\о\бр\ос\о\в\ест\н\о \ис\п\о\ль\зующ\и\й \и\мущ\ест\в\о \и ср\е\дст\в\а \пр\е\д\пр\ият\ия \в с\о\бст\в\е\н\ных \и\нт\ер\ес\ах \и\л\и \в \и\нт\ер\ес\ах \пр\от\и\в\о\п\о\л\о\ж\ных \и\нт\ер\ес\а\м учр\е\д\ит\е\л\е\й, \н\ес\ет \от\в\етст\в\е\н\н\ость \в \пр\е\д\е\л\ах, \о\пр\е\д\е\л\е\н\ных \гр\а\ж\д\а\нс\к\и\м, у\г\о\л\о\в\ны\м, \а\д\м\и\н\истр\ат\и\в\ны\м \пр\а\в\о\м.\r\n\r\n\Д\о\л\ж\н\ост\н\ая \и\нстру\кц\ия р\а\зр\а\б\от\а\н\а \в с\о\от\в\етст\в\и\и с уст\а\н\о\в\л\е\н\ны\м\и тр\е\б\о\в\а\н\ия\м\и.'),('e0abb512-9d93-4c76-889c-d8607857efad','РСЃРїРѕР»РЅРёС‚РµР»СЊРЅС‹Р№ СЃРѕС‚СЂСѓРґРЅРёРє',_binary '\О\бя\з\а\н\н\ост\и р\а\б\от\н\и\к\а\r\n\д\о\бр\ос\о\в\ест\н\о \ис\п\о\л\нять с\в\о\и тру\д\о\вы\е \о\бя\з\а\н\н\ост\и, \в\о\з\л\о\ж\е\н\ны\е \н\а \н\е\г\о тру\д\о\вы\м \д\о\г\о\в\ор\о\м; \r\nс\о\б\лю\д\ать \пр\а\в\и\л\а \в\нутр\е\н\н\е\г\о тру\д\о\в\о\г\о р\ас\п\оря\д\к\а \ор\г\а\н\и\з\ац\и\и; \r\nс\о\б\лю\д\ать тру\д\о\вую \д\исц\и\п\л\и\ну; \r\n\вы\п\о\л\нять уст\а\н\о\в\л\е\н\ны\е \н\ор\мы тру\д\а; \r\n','11000.00','10.00',_binary '   \Д\о\бр\ос\о\в\ест\н\о \ис\п\о\л\нять с\в\о\и тру\д\о\вы\е \о\бя\з\а\н\н\ост\и, \в\о\з\л\о\ж\е\н\ны\е \н\а \н\е\г\о тру\д\о\вы\м \д\о\г\о\в\ор\о\м; \r\nс\о\б\лю\д\ать \пр\а\в\и\л\а \в\нутр\е\н\н\е\г\о тру\д\о\в\о\г\о р\ас\п\оря\д\к\а \ор\г\а\н\и\з\ац\и\и; \r\nс\о\б\лю\д\ать тру\д\о\вую \д\исц\и\п\л\и\ну; \r\n\вы\п\о\л\нять уст\а\н\о\в\л\е\н\ны\е \н\ор\мы тру\д\а; \r\nс\о\б\лю\д\ать тр\е\б\о\в\а\н\ия \п\о \охр\а\н\е тру\д\а \и \о\б\ес\п\еч\е\н\ию \б\е\з\о\п\ас\н\ост\и тру\д\а; \r\n\б\ер\е\ж\н\о \от\н\ос\иться \к \и\мущ\ест\ву р\а\б\от\о\д\ат\е\ля \и \дру\г\их р\а\б\от\н\и\к\о\в; \r\n\н\е\з\а\м\е\д\л\ит\е\ль\н\о с\о\о\бщ\ить р\а\б\от\о\д\ат\е\лю \л\и\б\о \н\е\п\оср\е\дст\в\е\н\н\о\му ру\к\о\в\о\д\ит\е\лю \о \в\о\з\н\и\к\н\о\в\е\н\и\и с\иту\ац\и\и, \пр\е\дст\а\в\ляющ\е\й у\гр\о\зу \ж\и\з\н\и \и \з\д\ор\о\вью \лю\д\е\й, с\охр\а\н\н\ост\и \и\мущ\ест\в\а р\а\б\от\о\д\ат\е\ля.'),('3f8ca833-200c-4d1c-9586-807a4baa4faf','РњРµРЅРµРґР¶РµСЂ РїРѕ СЂР°Р±РѕС‚Рµ СЃ РєР»РёРµРЅС‚Р°РјРё',_binary '\М\е\н\е\д\ж\ер \п\о р\а\б\от\е с \к\л\и\е\нт\а\м\и \от\н\ос\ится \к \к\ат\е\г\ор\и\и ру\к\о\в\о\д\ит\е\л\е\й.\r\n\Н\а \д\о\л\ж\н\ость \м\е\н\е\д\ж\ер\а \п\о р\а\б\от\е с \к\л\и\е\нт\а\м\и \н\а\з\н\ач\а\ется \л\иц\о, \и\м\еющ\е\е \высш\е\е \о\бр\а\з\о\в\а\н\и\е','10000.00','6',_binary '\Д\О\Л\Ж\Н\О\С\Т\НА\Я \И\Н\С\Т\Р\У\К\Ц\И\Я	\r\n\r\n\м\е\н\е\д\ж\еру \п\о р\а\б\от\е с \к\л\и\е\нт\а\м\и	\r\n\r\nI. \О\бщ\и\е \п\о\л\о\ж\е\н\ия \r\n\М\е\н\е\д\ж\ер \п\о р\а\б\от\е с \к\л\и\е\нт\а\м\и \от\н\ос\ится \к \к\ат\е\г\ор\и\и ру\к\о\в\о\д\ит\е\л\е\й.\r\n\Н\а \д\о\л\ж\н\ость \м\е\н\е\д\ж\ер\а \п\о р\а\б\от\е с \к\л\и\е\нт\а\м\и \н\а\з\н\ач\а\ется \л\иц\о, \и\м\еющ\е\е \r\n___________________________________\r\n(\высш\е\е; ср\е\д\н\е\е)\r\n\пр\оф\есс\и\о\н\а\ль\н\о\е (э\к\о\н\о\м\ич\ес\к\о\е) \о\бр\а\з\о\в\а\н\и\е, \д\о\п\о\л\н\ит\е\ль\ную \п\о\д\г\от\о\в\ку \п\о \м\е\н\е\д\ж\м\е\нту, ст\а\ж р\а\б\оты \в сф\ер\е \пр\е\д\пр\и\н\и\м\ат\е\льст\в\а \н\е \м\е\н\е\е \r\n___________________________________.\r\n(3 \л\ет; 5 \л\ет; \др.)\r\n\М\е\н\е\д\ж\ер \п\о р\а\б\от\е с \к\л\и\е\нт\а\м\и \д\о\л\ж\е\н \з\н\ать:\r\n3.1. \З\а\к\о\н\о\д\ат\е\льст\в\о, р\е\г\л\а\м\е\нт\ирующ\е\е \в\о\пр\осы \осущ\ест\в\л\е\н\ия \пр\е\д\пр\и\н\и\м\ат\е\льс\к\о\й \д\еят\е\ль\н\ост\и.\r\n3.2. \Ры\н\оч\ную э\к\о\н\о\м\и\ку, \пр\е\д\пр\и\н\и\м\ат\е\льст\в\о \и \ос\н\о\вы \в\е\д\е\н\ия \б\и\з\н\ес\а.\r\n3.3. \Ос\н\о\вы \м\ар\к\ет\и\н\г\а (\к\о\нц\е\пц\ию \м\ар\к\ет\и\н\г\а, \ос\н\о\вы у\пр\а\в\л\е\н\ия \м\ар\к\ет\и\н\г\о\м, с\п\ос\о\бы \и \н\а\пр\а\в\л\е\н\ия \исс\л\е\д\о\в\а\н\и\й ры\н\к\а).\r\n3.4. \Т\е\ор\ию \м\е\н\е\д\ж\м\е\нт\а, \м\а\кр\о— \и \м\и\кр\оэ\к\о\н\о\м\и\к\и, \д\е\л\о\в\о\г\о \а\д\м\и\н\истр\ир\о\в\а\н\ия.\r\n3.5. Асс\орт\и\м\е\нт, \к\л\асс\иф\и\к\ац\ию, х\ар\а\кт\ер\ист\и\ку \и \н\а\з\н\ач\е\н\и\е \пр\е\д\л\а\г\а\е\мых т\о\в\ар\о\в (\пр\о\ду\кц\и\и).\r\n3.6. \П\оря\д\о\к р\а\зр\а\б\от\к\и \б\и\з\н\ес-\п\л\а\н\о\в \и \к\о\м\м\ерч\ес\к\их ус\л\о\в\и\й с\о\г\л\аш\е\н\и\й, \д\о\г\о\в\ор\о\в.\r\n3.7. \Пр\а\в\и\л\а уст\а\н\о\в\л\е\н\ия \д\е\л\о\вых \к\о\нт\а\кт\о\в.\r\n3.8. \Пр\а\в\и\л\а \оф\иц\и\а\ль\н\о\г\о эт\и\к\ет\а \пр\и \пр\о\в\е\д\е\н\и\и \п\ер\е\г\о\в\ор\о\в с \к\л\и\е\нт\а\м\и. \r\n3.9. \Т\е\ор\ию \м\е\ж\л\ич\н\ост\н\о\г\о \о\бщ\е\н\ия.\r\n3.10. \Ос\н\о\вы с\оц\и\о\л\о\г\и\и \и \пс\их\о\л\о\г\и\и.\r\n3.11. \М\ет\о\ды \о\бр\а\б\от\к\и \и\нф\ор\м\ац\и\и с \ис\п\о\ль\з\о\в\а\н\и\е\м с\о\вр\е\м\е\н\ных т\ех\н\ич\ес\к\их ср\е\дст\в \к\о\м\му\н\и\к\ац\и\и \и с\вя\з\и, \к\о\м\пьют\ер\а.\r\n\Н\а\з\н\ач\е\н\и\е \н\а \д\о\л\ж\н\ость \м\е\н\е\д\ж\ер\а \п\о р\а\б\от\е с \к\л\и\е\нт\а\м\и \и \ос\в\о\б\о\ж\д\е\н\и\е \от \д\о\л\ж\н\ост\и \пр\о\и\з\в\о\д\ится \пр\и\к\а\з\о\м ру\к\о\в\о\д\ит\е\ля \пр\е\д\пр\ият\ия \п\о \пр\е\дст\а\в\л\е\н\ию\r\n______________________________________________.\r\n(\к\о\м\м\ерч\ес\к\о\г\о \д\ир\е\кт\ор\а \пр\е\д\пр\ият\ия; \и\н\о\г\о \д\о\л\ж\н\ост\н\о\г\о \л\иц\а)\r\n\М\е\н\е\д\ж\ер \п\о р\а\б\от\е с \к\л\и\е\нт\а\м\и \п\о\дч\и\ня\ется \н\е\п\оср\е\дст\в\е\н\н\о \r\n______________________________________________.\r\n(\к\о\м\м\ерч\ес\к\о\му \д\ир\е\кт\ору \пр\е\д\пр\ият\ия; \и\н\о\му \д\о\л\ж\н\ост\н\о\му \л\ицу)\r\n\Н\а \вр\е\мя \отсутст\в\ия \м\е\н\е\д\ж\ер\а \п\о р\а\б\от\е с \к\л\и\е\нт\а\м\и (\от\пус\к, \б\о\л\е\з\нь, \пр.) \е\г\о \о\бя\з\а\н\н\ост\и \вы\п\о\л\ня\ет \л\иц\о, \н\а\з\н\ач\е\н\н\о\е \в уст\а\н\о\в\л\е\н\н\о\м \п\оря\д\к\е. \Д\а\н\н\о\е \л\иц\о \пр\и\о\бр\ет\а\ет с\о\от\в\етст\вующ\и\е \пр\а\в\а \и \н\ес\ет \от\в\етст\в\е\н\н\ость \з\а \н\е\н\а\д\л\е\ж\ащ\е\е \ис\п\о\л\н\е\н\и\е \в\о\з\л\о\ж\е\н\ных \н\а \н\е\г\о \о\бя\з\а\н\н\ост\е\й.\r\n\r\n \r\n\r\nII. \Д\о\л\ж\н\ост\ны\е \о\бя\з\а\н\н\ост\и \r\n\r\n\М\е\н\е\д\ж\ер \п\о р\а\б\от\е с \к\л\и\е\нт\а\м\и:\r\n\Осущ\ест\в\ля\ет \а\н\а\л\и\з \ау\д\ит\ор\и\и \п\от\е\нц\и\а\ль\ных \к\л\и\е\нт\о\в, \выя\в\ля\ет \п\отр\е\б\н\ост\и \к\л\и\е\нт\о\в, \их ур\о\в\е\нь \и \н\а\пр\а\в\л\е\н\н\ость.\r\n\Р\а\зр\а\б\аты\в\а\ет \м\ет\о\д\и\к\и \п\о\ис\к\а \к\л\и\е\нт\о\в, \п\л\а\н\иру\ет р\а\б\оту с \к\л\и\е\нт\а\м\и, с\ост\а\в\ля\ет сх\е\мы \о\бр\ащ\е\н\ия \к \к\л\и\е\нт\а\м.\r\n\Н\е\п\оср\е\дст\в\е\н\н\о \осущ\ест\в\ля\ет \п\о\ис\к \к\л\и\е\нт\о\в \вс\е\м\и \д\осту\п\ны\м\и с\п\ос\о\б\а\м\и (\пут\е\м р\а\з\м\ещ\е\н\ия р\е\к\л\а\мы, уч\аст\ия \в \выст\а\в\к\ах, яр\м\ар\к\ах, \пр\е\з\е\нт\ац\иях, \н\а\пр\а\в\л\е\н\ия \пр\е\д\л\о\ж\е\н\и\й \п\о ср\е\дст\в\а\м \к\о\м\му\н\и\к\ац\и\й, э\л\е\ктр\о\н\н\о\й \п\очт\о\й, ф\а\кс\и\м\и\ль\ны\м\и с\о\о\бщ\е\н\ия\м\и, \пр.).\r\n\Пр\о\г\н\о\з\иру\ет \д\е\л\о\вую \н\а\д\е\ж\н\ость \п\от\е\нц\и\а\ль\ных \к\л\и\е\нт\о\в, \их ф\и\н\а\нс\о\вую \и \м\ат\ер\и\а\ль\ную \о\б\ес\п\еч\е\н\н\ость.\r\n\Ор\г\а\н\и\зу\ет \и \пр\о\в\о\д\ит \пр\е\д\в\ар\ит\е\ль\ны\е \п\ер\е\г\о\в\оры с \к\л\и\е\нт\а\м\и, \з\а\и\нт\ер\ес\о\в\а\вш\и\м\ися \пр\е\д\л\о\ж\е\н\ия\м\и (\пр\и\ня\вш\и\м\и \оф\ерту, \пр.), ут\оч\ня\ет \п\отр\е\б\н\ост\и \к\а\ж\д\о\г\о \к\о\н\кр\ет\н\о\г\о \к\л\и\е\нт\а \и \п\о\д\г\от\а\в\л\и\в\а\ет \пр\е\д\л\о\ж\е\н\и\е, \а\др\ес\о\в\а\н\н\о\е \о\пр\е\д\е\л\е\н\н\о\му \к\л\и\е\нту.\r\n\Встр\еч\а\ется с \к\л\и\е\нт\а\м\и, у\б\е\ж\д\а\ет \к\л\и\е\нт\о\в \в \вы\г\о\д\н\ост\и \пр\е\д\л\о\ж\е\н\ия, \пр\е\д\л\а\г\а\ет \н\а \о\бсу\ж\д\е\н\и\е \и с\о\г\л\ас\о\в\а\н\и\е \пр\о\е\кты \д\о\г\о\в\ор\о\в, \пр\и\н\и\м\а\ет уч\аст\и\е \в р\а\б\от\е \н\а\д с\о\г\л\ас\о\в\а\н\и\е\м р\а\з\н\о\г\л\ас\и\й, \з\а\к\люч\а\ет \д\о\г\о\в\оры \от \и\м\е\н\и \пр\е\д\пр\ият\ия.\r\n\Пр\е\д\л\а\г\а\ет \к\л\и\е\нт\а\м \пут\и р\еш\е\н\ия \н\е с\о\г\л\ас\о\в\а\н\ных \пр\и \п\ер\е\г\о\в\ор\ах \в\о\пр\ос\о\в \и \в\о\пр\ос\о\в, \в\о\з\н\и\кш\их \п\ос\л\е с\о\в\ерш\е\н\ия юр\и\д\ич\ес\к\и \з\н\ач\и\мых \д\е\йст\в\и\й.\r\n\П\о\д\д\ер\ж\и\в\а\ет \п\ост\оя\н\ны\й \к\о\нт\а\кт с сущ\ест\вующ\и\м\и \к\л\и\е\нт\а\м\и, \ор\г\а\н\и\зу\ет р\а\б\оту с \н\и\м\и \п\о уст\оя\вш\и\мся \д\е\л\о\вы\м сх\е\м\а\м.\r\n\Р\а\зр\а\б\аты\в\а\ет сх\е\мы \в\з\а\и\м\о\от\н\ош\е\н\и\й с \н\а\и\б\о\л\е\е \вы\г\о\д\ны\м\и \и \п\ерс\п\е\кт\и\в\ны\м\и \к\л\и\е\нт\а\м\и (\пр\е\д\л\о\ж\е\н\ия \ос\о\бых ус\л\о\в\и\й \д\о\г\о\в\ор\о\в, с\ист\е\м с\к\и\д\о\к \и \и\н\д\и\в\и\ду\а\ль\н\о\г\о \о\бс\лу\ж\и\в\а\н\ия, ус\к\ор\е\н\ных ср\о\к\о\в \и \ос\о\бых ус\л\о\в\и\й \ис\п\о\л\н\е\н\ия \д\о\г\о\в\ор\ных \о\бя\з\ат\е\льст\в, \пр.).\r\n\Р\а\зр\а\б\аты\в\а\ет \и \д\а\ет \к\л\и\е\нт\а\м р\е\к\о\м\е\н\д\ац\и\и \и \к\о\нсу\льт\ац\и\и \п\о \н\а\и\б\о\л\е\е эфф\е\кт\и\в\н\о\му \ис\п\о\ль\з\о\в\а\н\ию уст\оя\вш\ихся \д\е\л\о\вых с\вя\з\е\й; \о\б\ес\п\еч\и\в\а\ет \в\о\з\м\о\ж\н\ость \п\ос\ещ\е\н\ия \к\л\и\е\нт\а\м\и \выст\а\в\о\к, яр\м\ар\о\к, \пр\е\з\е\нт\ац\и\й \н\о\вых \пр\о\ду\кт\о\в (т\о\в\ар\о\в, ус\лу\г).\r\n\О\б\ес\п\еч\и\в\а\ет с\о\б\лю\д\е\н\и\е \и\нт\ер\ес\о\в \к\л\и\е\нт\о\в \пр\и \вы\п\о\л\н\е\н\и\и ус\л\о\в\и\й \д\о\г\о\в\ор\о\в \п\о\др\а\з\д\е\л\е\н\ия\м\и \пр\е\д\пр\ият\ия.\r\n\Н\а\л\а\ж\и\в\а\ет \о\бр\ат\ную с\вя\зь с \к\л\и\е\нт\а\м\и (\и\зуч\а\ет \их тр\е\б\о\в\а\н\ия \к \пр\о\ду\кц\и\и (т\о\в\ар\а\м, ус\лу\г\а\м), уст\а\н\а\в\л\и\в\а\ет \пр\ич\и\ны \н\еу\д\о\в\л\ет\в\ор\е\н\н\ост\и \к\л\и\е\нт\а с\о\в\м\ест\н\о\й р\а\б\от\о\й, \а\н\а\л\и\з\иру\ет \пр\ет\е\н\з\и\и \к\л\и\е\нт\о\в \и \пр\и\н\и\м\а\ет \вс\е \м\еры \п\о \их р\еш\е\н\ию \и с\охр\а\н\е\н\ию \д\е\л\о\вых с\вя\з\е\й).\r\n\Ф\ор\м\иру\ет \б\а\н\к \д\а\н\ных \о \к\л\и\е\нт\ах (\к\л\и\е\нтс\кую \б\а\зу), с\в\о\е\вр\е\м\е\н\н\о \в\н\ос\ит \в \н\е\е \и\з\м\е\н\е\н\ия.\r\n\И\зуч\а\ет \и \а\н\а\л\и\з\иру\ет \п\о\л\ит\и\ку \к\о\н\кур\е\нт\о\в \в\о \в\з\а\и\м\о\от\н\ош\е\н\иях с \к\л\и\е\нт\а\м\и.\r\n\r\n \r\n\r\nIII. \Пр\а\в\а \r\n\r\n\М\е\н\е\д\ж\ер \п\о р\а\б\от\е с \к\л\и\е\нт\а\м\и \и\м\е\ет \пр\а\в\о:\r\n\С\а\м\ост\оят\е\ль\н\о \о\пр\е\д\е\лять ф\ор\мы р\а\б\оты с \к\л\и\е\нт\а\м\и, с\п\ос\о\бы уст\а\н\о\в\л\е\н\ия \д\е\л\о\вых с\вя\з\е\й.\r\n\Р\ас\п\оря\ж\аться \в\в\ер\е\н\ны\м\и \е\му ф\и\н\а\нс\о\вы\м\и ср\е\дст\в\а\м\и (\н\а \пр\е\дст\а\в\ит\е\льс\к\и\е р\асх\о\ды).\r\n\П\о\д\п\исы\в\ать \и \в\и\з\ир\о\в\ать \д\о\ку\м\е\нты \в \пр\е\д\е\л\ах с\в\о\е\й \к\о\м\п\ет\е\нц\и\и.\r\n\З\н\а\к\о\м\иться с \д\о\ку\м\е\нт\а\м\и, \о\пр\е\д\е\ляющ\и\м\и \е\г\о \пр\а\в\а \и \о\бя\з\а\н\н\ост\и \п\о \з\а\н\и\м\а\е\м\о\й \д\о\л\ж\н\ост\и, \кр\ит\ер\и\и \оц\е\н\к\и \к\ач\ест\в\а \ис\п\о\л\н\е\н\ия \д\о\л\ж\н\ост\ных \о\бя\з\а\н\н\ост\е\й.\r\n\З\а\пр\аш\и\в\ать \от ру\к\о\в\о\дст\в\а \и с\п\ец\и\а\л\ист\о\в \п\о\др\а\з\д\е\л\е\н\и\й \пр\е\д\пр\ият\ия \и\нф\ор\м\ац\ию \и \д\о\ку\м\е\нты, \н\е\о\бх\о\д\и\мы\е \д\ля \вы\п\о\л\н\е\н\ия \е\г\о \д\о\л\ж\н\ост\ных \о\бя\з\а\н\н\ост\е\й.\r\n\Тр\е\б\о\в\ать \от ру\к\о\в\о\дст\в\а \пр\е\д\пр\ият\ия \о\б\ес\п\еч\е\н\ия \ор\г\а\н\и\з\ац\и\о\н\н\о-т\ех\н\ич\ес\к\их ус\л\о\в\и\й \и \оф\ор\м\л\е\н\ия уст\а\н\о\в\л\е\н\ных \д\о\ку\м\е\нт\о\в, \н\е\о\бх\о\д\и\мых \д\ля \ис\п\о\л\н\е\н\ия \д\о\л\ж\н\ост\ных \о\бя\з\а\н\н\ост\е\й.\r\n\r\n \r\n\r\nIV. \От\в\етст\в\е\н\н\ость \r\n\r\n\М\е\н\е\д\ж\ер \п\о р\а\б\от\е с \к\л\и\е\нт\а\м\и \н\ес\ет \от\в\етст\в\е\н\н\ость:\r\n\З\а \н\е\н\а\д\л\е\ж\ащ\е\е \ис\п\о\л\н\е\н\и\е \и\л\и \н\е\ис\п\о\л\н\е\н\и\е с\в\о\их \д\о\л\ж\н\ост\ных \о\бя\з\а\н\н\ост\е\й, \пр\е\дус\м\отр\е\н\ных \н\аст\оящ\е\й \д\о\л\ж\н\ост\н\о\й \и\нстру\кц\и\е\й, — \в \пр\е\д\е\л\ах, уст\а\н\о\в\л\е\н\ных \д\е\йст\вующ\и\м тру\д\о\вы\м \з\а\к\о\н\о\д\ат\е\льст\в\о\м \Р\осс\и\йс\к\о\й \Ф\е\д\ер\ац\и\и.\r\n\З\а \пр\а\в\о\н\аруш\е\н\ия, с\о\в\ерш\е\н\ны\е \в \пр\оц\есс\е с\в\о\е\й \д\еят\е\ль\н\ост\и, — \в \пр\е\д\е\л\ах, уст\а\н\о\в\л\е\н\ных \д\е\йст\вующ\и\м \а\д\м\и\н\истр\ат\и\в\ны\м, у\г\о\л\о\в\ны\м \и \гр\а\ж\д\а\нс\к\и\м \з\а\к\о\н\о\д\ат\е\льст\в\о\м \Р\осс\и\йс\к\о\й \Ф\е\д\ер\ац\и\и.\r\n\З\а \пр\ич\и\н\е\н\и\е \м\ат\ер\и\а\ль\н\о\г\о ущ\ер\б\а \пр\е\д\пр\ият\ию — \в \пр\е\д\е\л\ах, уст\а\н\о\в\л\е\н\ных \д\е\йст\вующ\и\м тру\д\о\вы\м \и \гр\а\ж\д\а\нс\к\и\м \з\а\к\о\н\о\д\ат\е\льст\в\о\м \Р\осс\и\йс\к\о\й \Ф\е\д\ер\ац\и\и.\r\n\r\n \r\n 	\r\n 	\r\n 	\r\n 	\r\n \r\n\r\n 	\r\n 	\r\n_____________	\r\n 	\r\n____________________\r\n\r\n(с\ост\а\в\ит\е\ль \д\о\л\ж\н\ост\н\о\й \и\нстру\кц\и\и)	\r\n 	\r\n(\п\о\д\п\ись)	\r\n 	\r\n(р\асш\ифр\о\в\к\а \п\о\д\п\ис\и)\r\n\r\n 	\r\n 	\r\n00.00.200_\г	\r\n 	\r\n \r\n\r\n 	\r\n 	\r\n 	\r\n 	\r\n \r\n\r\n 	\r\n 	\r\n 	\r\n 	\r\n \r\n\r\n(\л\иц\о, \в\и\з\ирующ\е\е \д\о\л\ж\н\ост\ную \и\нстру\кц\ию)	\r\n 	\r\n 	\r\n 	\r\n \r\n\r\n______________	\r\n 	\r\n____________________	\r\n 	\r\n 	\r\n 	\r\n \r\n\r\n(\п\о\д\п\ись)	\r\n 	\r\n(р\асш\ифр\о\в\к\а \п\о\д\п\ис\и)	\r\n 	\r\n 	\r\n 	\r\n \r\n\r\n00.00.200_\г	\r\n 	\r\n 	\r\n 	\r\n '),('eda27ae4-6bc1-4f18-ba0b-fbda08cbd49a','РќР°С‡Р°Р»СЊРЅРёРє РѕС‚РґРµР»Р°',_binary '\В \о\бя\з\а\н\н\ост\и \вх\о\д\ит\r\n-\У\пр\а\в\л\е\н\и\е \пр\е\д\пр\и\н\и\м\ат\е\льс\к\о\й \и\л\и \к\о\м\м\ерч\ес\к\о\й \д\еят\е\ль\н\остью \r\n\пр\е\д\пр\ият\ия. \r\n- \В\з\а\и\м\о\д\е\йст\в\и\е с \пр\е\дст\а\в\ит\е\ля\м\и ст\ор\о\н\н\их \ор\г\а\н\и\з\ац\и\й \в \r\n\пр\оц\есс\е \вы\п\о\л\н\е\н\ия фу\н\кц\и\о\н\а\ль\ных \о\бя\з\а\н\н\ост\е\й. \r\n- \П\о\д\б\ор \и р\асст\а\н\о\в\к\а \к\а\др\о\в, ст\и\му\л\ир\о','12000.00','2',_binary '1. \П\л\а\н\ир\о\в\а\н\и\е \пр\о\д\а\ж\r\n\r\n\Д\ля р\ац\и\о\н\а\ль\н\о\г\о \п\л\а\н\ир\о\в\а\н\ия \н\ач\а\ль\н\и\ку \от\д\е\л\а \пр\о\д\а\ж \н\е\о\бх\о\д\и\м\а \п\о\л\н\ая \и\нф\ор\м\ац\ия \о \пр\о\д\а\ж\ах \з\а \пр\ош\е\дш\и\й \п\ер\и\о\д: р\а\б\от\а т\ор\г\о\вых т\оч\е\к \и ф\и\л\и\а\л\о\в, \о\бъё\м \пр\о\д\а\ж \п\о р\а\з\ны\м \к\ат\е\г\ор\ия\м т\о\в\ар\о\в, р\е\зу\льт\ат\и\в\н\ость \пр\о\д\а\ж \м\е\н\е\д\ж\ер\о\в. \Вс\е эт\и \д\а\н\ны\е, \а т\а\к\ж\е \м\н\о\г\и\е \дру\г\и\е \пр\е\дст\а\в\ля\ет \в\а\м с\ист\е\м\а 24com. \С \п\о\м\ощью эт\о\г\о \м\о\ду\ля \отчёт\о\в \вы с\м\о\ж\ет\е с\ост\а\в\лять \м\ар\к\ет\и\н\г\о\вы\й \бю\д\ж\ет \и \п\л\а\н\ир\о\в\ать тр\аты \н\а р\е\к\л\а\му \и \пр\о\д\в\и\ж\е\н\и\е \пр\о\ду\кц\и\и.\r\n\r\n \r\n2. \С\о\в\ерш\е\нст\в\о\в\а\н\и\е с\ер\в\ис\а\r\n\r\n\К\о\н\еч\н\о, \лю\б\о\й \в\аш \м\е\н\е\д\ж\ер – ч\е\л\о\в\е\к с\о с\л\о\ж\и\вш\и\м\ися ч\ерт\а\м\и х\ар\а\кт\ер\а, у\б\е\ж\д\е\н\ия\м\и \и \пр\и\выч\к\а\м\и, \и\з\м\е\н\ить \к\от\оры\е \пр\а\кт\ич\ес\к\и \н\е\в\о\з\м\о\ж\н\о. \Н\о \н\ач\а\ль\н\и\к \от\д\е\л\а \пр\о\д\а\ж \д\о\л\ж\е\н \з\а\б\от\иться \о т\о\м, чт\о\бы \к\а\ж\ды\й \к\л\и\е\нт \к\о\м\п\а\н\и\и \бы\л \о\бс\лу\ж\е\н \н\а \выс\о\к\о\м ур\о\в\н\е \в\н\е \з\а\в\ис\и\м\ост\и \от \л\ич\н\ост\и \и \к\в\а\л\иф\и\к\ац\и\и с\отру\д\н\и\к\а. \С\ист\е\м\а учёт\а 24com \п\о\м\о\ж\ет \в\а\м сф\ор\му\л\ир\о\в\ать тр\е\б\о\в\а\н\ия \к ур\о\в\ню \о\бс\лу\ж\и\в\а\н\ия \к\л\и\е\нт\о\в \и \к\о\нтр\о\л\ир\о\в\ать \их \вы\п\о\л\н\е\н\и\е \к\а\ж\ды\м \м\е\н\е\д\ж\ер\о\м.\r\n\r\n \r\n3. \Н\ач\а\ль\н\и\к \от\д\е\л\а \пр\о\д\а\ж, \м\от\и\в\иру\й с\отру\д\н\и\к\о\в!\r\n\r\n\С\л\о\ж\и\л\ись \н\ес\к\о\ль\к\о с\ист\е\м \м\ат\ер\и\а\ль\н\о\й \м\от\и\в\ац\и\и с\отру\д\н\и\к\о\в, р\а\б\от\ающ\их с \к\л\и\е\нт\а\м\и. \Н\е\и\з\м\е\н\н\ая ст\а\в\к\а \и \и\з\м\е\няющ\и\йся \в \з\а\в\ис\и\м\ост\и \от \о\бъё\м\а \пр\о\д\а\ж \пр\оц\е\нт - с\а\м\ая \и\з\в\ест\н\ая, \н\о \и с\а\м\ая \н\ес\о\в\ерш\е\н\н\ая с\ист\е\м\а \п\о\ощр\е\н\ия, \в\е\дь \пр\и т\а\к\о\м \п\о\дх\о\д\е \м\е\н\е\д\ж\ер \н\е\ох\от\н\о \б\ерётся \з\а \вы\п\о\л\н\е\н\и\е \н\е\б\о\льш\их \з\а\к\а\з\о\в! \Н\а\л\иц\о – ст\о\л\к\н\о\в\е\н\и\е \и\нт\ер\ес\о\в \к\о\м\п\а\н\и\и \и с\отру\д\н\и\к\а. \Чт\о\бы эт\о\г\о \и\з\б\е\ж\ать, \в\ос\п\о\ль\зу\йт\есь с\ист\е\м\о\й \отчёт\о\в 24com, \г\д\е \п\о\др\о\б\ны\е \д\а\н\ны\е \о \пр\о\д\а\ж\ах \д\а\дут \в\о\з\м\о\ж\н\ость \а\н\а\л\и\з\ир\о\в\ать \и\нф\ор\м\ац\ию \и \д\а\в\ать \б\о\нусы \з\а р\е\а\ль\ны\е \з\ас\лу\г\и, \н\а\пр\и\м\ер, \з\а \к\ач\ест\в\е\н\н\о\е \пр\и\в\л\еч\е\н\и\е \и \пр\о\в\е\д\е\н\и\е \к\л\и\е\нт\а. \Т\а\к\ж\е \н\ач\а\ль\н\и\к \от\д\е\л\а \пр\о\д\а\ж с\м\о\ж\ет уст\а\н\о\в\ить \пр\оц\е\нт \п\о\ощр\е\н\ия \м\е\н\е\д\ж\ер\а, \з\а\в\исящ\и\й \н\е т\о\ль\к\о \от \о\бъё\м\а \пр\о\д\а\ж, \н\о \и \от \м\ар\ж\и \и\л\и \дру\г\их \п\о\к\а\з\ат\е\л\е\й.\r\n\r\n \r\n4. \Эфф\е\кт\и\в\н\о\е у\пр\а\в\л\е\н\и\е \от\д\е\л\о\м \пр\о\д\а\ж\r\n\r\n\О\д\н\а \и\з \о\бя\з\а\н\н\ост\е\й \н\ач\а\ль\н\и\к\а \от\д\е\л\а \пр\о\д\а\ж - чёт\к\о \к\о\нтр\о\л\ир\о\в\ать т\е\кущую р\а\б\оту \к\а\ж\д\о\г\о с\отру\д\н\и\к\а. \Н\о \д\е\л\ать эт\о \ну\ж\н\о эфф\е\кт\и\в\н\о. \Отчёт\н\ость 24com \п\о\м\о\ж\ет \в эт\о\м, \в\а\м \д\а\ж\е \н\е \н\а\д\о \бу\д\ет \отх\о\д\ить \от \в\аш\е\г\о \к\о\м\пьют\ер\а! \Ес\л\и \вы \п\о\ль\зу\ет\есь с\ист\е\м\о\й 24com, т\о \и\м\е\ет\е \в\о\з\м\о\ж\н\ость \п\о\л\н\остью \к\о\нтр\о\л\ир\о\в\ать р\а\б\оту с\в\о\е\г\о \от\д\е\л\а \и \к\а\ж\д\о\г\о \м\е\н\е\д\ж\ер\а, \и\м\ея \д\осту\п \к \вс\е\м\ир\н\о\й с\вя\з\и. \П\ер\е\д\а\в\ать \п\о\л\н\о\м\оч\ия, \п\оруч\ать \д\е\л\а, с\л\е\д\ить \з\а х\о\д\о\м \их \вы\п\о\л\н\е\н\ия – \в\от \н\е\п\о\л\ны\й с\п\ис\о\к т\о\г\о, чт\о \вы с\м\о\ж\ет\е \д\е\л\ать. \С\ист\е\м\а 24com \п\о\м\о\ж\ет \в\а\м \к\а\к \в \д\о\л\г\оср\оч\н\о\м \п\л\а\н\ир\о\в\а\н\и\и р\а\б\оты \от\д\е\л\а, т\а\к \и \в т\е\кущ\их \е\ж\е\д\н\е\в\ных \д\е\л\ах.'),('b74fa7ce-ae24-4f84-b5b1-0ff1ec04fc12','РЎС‚Р°СЂС€РёР№ РјРµРЅРµРґР¶РµСЂ',_binary ' \Н\а \д\о\л\ж\н\ость \м\е\н\е\д\ж\ер\а \н\а\з\н\ач\а\ется \л\иц\о, \и\м\еющ\е\е \высш\е\е \r\n\пр\оф\есс\и\о\н\а\ль\н\о\е \о\бр\а\з\о\в\а\н\и\е (\п\о с\п\ец\и\а\ль\н\ост\и \м\е\н\е\д\ж\м\е\нт) \и\л\и \высш\е\е \r\n\пр\оф\есс\и\о\н\а\ль\н\о\е \о\бр\а\з\о\в\а\н\и\е \и \д\о\п\о\л\н\ит\е\ль\ную \п\о\д\г\от\о\в\ку \в \о\б\л\аст\и т\е\ор\и\и \r\n\и \пр\а\кт\и\к\и \м\е\н\е\д\ж\м\е\нт\а, ','15000.00','3',_binary '\Д\о\л\ж\н\ост\н\ая \и\нстру\кц\ия \r\n\м\е\н\е\д\ж\ер\а 1\r\n______________________________________________ \r\n(\н\а\и\м\е\н\о\в\а\н\и\е \ор\г\а\н\и\з\ац\и\и, \пр\е\д\пр\ият\ия \и т.\п.) \r\n\r\n\" \" ____________ 20__\г. N__________ \r\n\r\n\r\n\Н\аст\оящ\ая \д\о\л\ж\н\ост\н\ая \и\нстру\кц\ия р\а\зр\а\б\от\а\н\а \и ут\в\ер\ж\д\е\н\а \н\а \r\n\ос\н\о\в\а\н\и\и тру\д\о\в\о\г\о \д\о\г\о\в\ор\а с __________________________________________ \r\n(\н\а\и\м\е\н\о\в\а\н\и\е \д\о\л\ж\н\ост\и \л\иц\а, \н\а \к\от\ор\о\г\о \r\n______________________________________________________ \и \в с\о\от\в\етст\в\и\и с \r\nс\ост\а\в\л\е\н\а \н\аст\оящ\ая \д\о\л\ж\н\ост\н\ая \и\нстру\кц\ия) \r\n\п\о\л\о\ж\е\н\ия\м\и \Тру\д\о\в\о\г\о \к\о\д\е\кс\а \Р\осс\и\йс\к\о\й \Ф\е\д\ер\ац\и\и \и \и\ных \н\ор\м\ат\и\в\ных \r\n\а\кт\о\в, р\е\гу\л\ирующ\их тру\д\о\вы\е \пр\а\в\о\от\н\ош\е\н\ия \в \Р\осс\и\йс\к\о\й \Ф\е\д\ер\ац\и\и. \r\n\r\nI. \О\бщ\и\е \п\о\л\о\ж\е\н\ия \r\n\r\n1.1. \М\е\н\е\д\ж\ер \от\н\ос\ится \к \к\ат\е\г\ор\и\и ру\к\о\в\о\д\ит\е\л\е\й. \Пр\и\н\и\м\а\ется \н\а \r\nр\а\б\оту \и у\в\о\ль\ня\ется с \н\е\е \пр\и\к\а\з\о\м \д\ир\е\кт\ор\а \пр\е\д\пр\ият\ия. \r\n1.2. \М\е\н\е\д\ж\ер \н\е\п\оср\е\дст\в\е\н\н\о \п\о\дч\и\ня\ется __________________________ \r\n________________________________________________________________________. \r\n1.3. \Н\а \д\о\л\ж\н\ость \м\е\н\е\д\ж\ер\а \н\а\з\н\ач\а\ется \л\иц\о, \и\м\еющ\е\е \высш\е\е \r\n\пр\оф\есс\и\о\н\а\ль\н\о\е \о\бр\а\з\о\в\а\н\и\е (\п\о с\п\ец\и\а\ль\н\ост\и \м\е\н\е\д\ж\м\е\нт) \и\л\и \высш\е\е \r\n\пр\оф\есс\и\о\н\а\ль\н\о\е \о\бр\а\з\о\в\а\н\и\е \и \д\о\п\о\л\н\ит\е\ль\ную \п\о\д\г\от\о\в\ку \в \о\б\л\аст\и т\е\ор\и\и \r\n\и \пр\а\кт\и\к\и \м\е\н\е\д\ж\м\е\нт\а, ст\а\ж р\а\б\оты \п\о с\п\ец\и\а\ль\н\ост\и \н\е \м\е\н\е\е_______ \л\ет. \r\n1.4. \В\о \вр\е\мя \отсутст\в\ия \м\е\н\е\д\ж\ер\а (\к\о\м\а\н\д\ир\о\в\к\а, \от\пус\к, \б\о\л\е\з\нь \и \r\n\пр.) \е\г\о \д\о\л\ж\н\ост\ны\е \о\бя\з\а\н\н\ост\и \вы\п\о\л\ня\ет \з\а\м\ест\ит\е\ль, \н\а\з\н\ач\а\е\мы\й \в \r\nуст\а\н\о\в\л\е\н\н\о\м \п\оря\д\к\е, \к\от\оры\й \н\ес\ет \п\о\л\ную \от\в\етст\в\е\н\н\ость \з\а \к\ач\ест\в\о \и \r\nс\в\о\е\вр\е\м\е\н\н\ость \их \вы\п\о\л\н\е\н\ия. \r\n1.5.\В с\в\о\е\й \д\еят\е\ль\н\ост\и \м\е\н\е\д\ж\ер ру\к\о\в\о\дст\ву\ется: \r\n- \з\а\к\о\н\о\д\ат\е\ль\ны\м\и \и \н\ор\м\ат\и\в\ны\м\и \д\о\ку\м\е\нт\а\м\и \п\о \в\о\пр\ос\а\м \r\n\пр\е\д\пр\и\н\и\м\ат\е\льс\к\о\й \и \к\о\м\м\ерч\ес\к\о\й \д\еят\е\ль\н\ост\и; \r\n- \м\ет\о\д\ич\ес\к\и\м\и \м\ат\ер\и\а\л\а\м\и, \к\ас\ающ\и\м\ися с\о\от\в\етст\вующ\их \в\о\пр\ос\о\в; \r\n- уст\а\в\о\м \пр\е\д\пр\ият\ия; \r\n- \пр\а\в\и\л\а\м\и тру\д\о\в\о\г\о р\ас\п\оря\д\к\а; \r\n- \пр\и\к\а\з\а\м\и, р\ас\п\оря\ж\е\н\ия\м\и \д\ир\е\кт\ор\а \пр\е\д\пр\ият\ия, \н\е\п\оср\е\дст\в\е\н\н\о\г\о \r\nру\к\о\в\о\д\ит\е\ля; \r\n- \н\аст\оящ\е\й \д\о\л\ж\н\ост\н\о\й \и\нстру\кц\и\е\й. \r\n1.6. \М\е\н\е\д\ж\ер \д\о\л\ж\е\н \з\н\ать: \r\n- \з\а\к\о\н\о\д\ат\е\ль\ны\е \и \н\ор\м\ат\и\в\ны\е \а\кты, р\е\гу\л\ирующ\и\е \r\n\пр\е\д\пр\и\н\и\м\ат\е\льс\кую \д\еят\е\ль\н\ость; \r\n- ры\н\оч\ную э\к\о\н\о\м\и\ку, \пр\е\д\пр\и\н\и\м\ат\е\льст\в\о \и \в\е\д\е\н\и\е \б\и\з\н\ес\а; \r\n- \к\о\нъю\н\ктуру ры\н\к\а, \п\оря\д\о\к ц\е\н\о\о\бр\а\з\о\в\а\н\ия, \н\а\л\о\г\о\о\б\л\о\ж\е\н\ия, \r\n\ос\н\о\вы \м\ар\к\ет\и\н\г\а; \r\n- т\е\ор\ию \м\е\н\е\д\ж\м\е\нт\а, \м\а\кр\о- \и \м\и\кр\оэ\к\о\н\о\м\и\к\и, \д\е\л\о\в\о\г\о \r\n\а\д\м\и\н\истр\ир\о\в\а\н\ия, \б\ир\ж\е\в\о\г\о, стр\ах\о\в\о\г\о, \б\а\н\к\о\вс\к\о\г\о \и ф\и\н\а\нс\о\в\о\г\о \д\е\л\а; \r\n- т\е\ор\ию \и \пр\а\кт\и\ку р\а\б\оты с \п\ерс\о\н\а\л\о\м; \r\n- ф\ор\мы \и \м\ет\о\ды \в\е\д\е\н\ия р\е\к\л\а\м\ных \к\а\м\п\а\н\и\й; \r\n- \п\оря\д\о\к р\а\зр\а\б\от\к\и \б\и\з\н\ес-\п\л\а\н\о\в \и \к\о\м\м\ерч\ес\к\их ус\л\о\в\и\й \r\nс\о\г\л\аш\е\н\и\й, \д\о\г\о\в\ор\о\в, \к\о\нтр\а\кт\о\в; \r\n- \ос\н\о\вы с\оц\и\о\л\о\г\и\и, \пс\их\о\л\о\г\и\и \и \м\от\и\в\ац\и\и тру\д\а; \r\n- эт\и\ку \д\е\л\о\в\о\г\о \о\бщ\е\н\ия; \r\n- \ос\н\о\вы т\ех\н\о\л\о\г\и\и \пр\о\и\з\в\о\дст\в\а; \r\n- стру\ктуру у\пр\а\в\л\е\н\ия \пр\е\д\пр\ият\и\е\м, \п\ерс\п\е\кт\и\вы \и\н\н\о\в\ац\и\о\н\н\о\й \и \r\n\и\н\в\ест\иц\и\о\н\н\о\й \д\еят\е\ль\н\ост\и; \r\n- \м\ет\о\ды \оц\е\н\к\и \д\е\л\о\вых \к\ач\ест\в р\а\б\от\н\и\к\о\в; \r\n- \ос\н\о\вы \д\е\л\о\пр\о\и\з\в\о\дст\в\а; \r\n- \м\ет\о\ды \о\бр\а\б\от\к\и \и\нф\ор\м\ац\и\и с \ис\п\о\ль\з\о\в\а\н\и\е\м с\о\вр\е\м\е\н\ных \r\nт\ех\н\ич\ес\к\их ср\е\дст\в, \к\о\м\му\н\и\к\ац\и\й \и с\вя\з\и, \выч\ис\л\ит\е\ль\н\о\й т\ех\н\и\к\и; \r\n- \з\а\к\о\н\о\д\ат\е\льст\в\о \о тру\д\е; \r\n- \п\ер\е\д\о\в\о\й \от\еч\ест\в\е\н\ны\й \и \з\ару\б\е\ж\ны\й \о\пыт \в \о\б\л\аст\и \м\е\н\е\д\ж\м\е\нт\а; \r\n- \пр\а\в\и\л\а \и \н\ор\мы \охр\а\ны тру\д\а. \r\n\r\nII. \Фу\н\кц\и\и \r\n\r\n\Н\а \м\е\н\е\д\ж\ер\а \в\о\з\л\а\г\аются с\л\е\дующ\и\е фу\н\кц\и\и: \r\n2.1. \У\пр\а\в\л\е\н\и\е \пр\е\д\пр\и\н\и\м\ат\е\льс\к\о\й \и\л\и \к\о\м\м\ерч\ес\к\о\й \д\еят\е\ль\н\остью \r\n\пр\е\д\пр\ият\ия. \r\n2.2. \В\з\а\и\м\о\д\е\йст\в\и\е с \пр\е\дст\а\в\ит\е\ля\м\и ст\ор\о\н\н\их \ор\г\а\н\и\з\ац\и\й \в \r\n\пр\оц\есс\е \вы\п\о\л\н\е\н\ия фу\н\кц\и\о\н\а\ль\ных \о\бя\з\а\н\н\ост\е\й. \r\n2.3. \П\о\д\б\ор \и р\асст\а\н\о\в\к\а \к\а\др\о\в, ст\и\му\л\ир\о\в\а\н\и\е \к\ач\ест\в\а тру\д\а. \r\n2.4. \Ор\г\а\н\и\з\ац\ия р\е\к\л\а\м\н\о\й \д\еят\е\ль\н\ост\и. \r\n\r\nIII. \Д\о\л\ж\н\ост\ны\е \о\бя\з\а\н\н\ост\и \r\n\r\n\Д\ля \вы\п\о\л\н\е\н\ия \в\о\з\л\о\ж\е\н\ных \н\а \н\е\г\о фу\н\кц\и\й \м\е\н\е\д\ж\ер \о\бя\з\а\н: \r\n3.1. \Осущ\ест\в\лять у\пр\а\в\л\е\н\и\е \пр\е\д\пр\и\н\и\м\ат\е\льс\к\о\й \и\л\и \к\о\м\м\ерч\ес\к\о\й \r\n\д\еят\е\ль\н\остью \пр\е\д\пр\ият\ия, \н\а\пр\а\в\л\е\н\н\о\й \н\а у\д\о\в\л\ет\в\ор\е\н\и\е \ну\ж\д \r\n\п\отр\е\б\ит\е\л\е\й \и \п\о\луч\е\н\и\е \пр\и\бы\л\и \з\а сч\ет ст\а\б\и\ль\н\о\г\о фу\н\кц\и\о\н\ир\о\в\а\н\ия, \r\n\п\о\д\д\ер\ж\а\н\ия \д\е\л\о\в\о\й р\е\пут\ац\и\и \и \в с\о\от\в\етст\в\и\и с \пр\е\д\ост\а\в\л\е\н\ны\м\и \r\n\п\о\л\н\о\м\оч\ия\м\и \и \вы\д\е\л\е\н\ны\м\и р\есурс\а\м\и. \r\n3.2. \Исх\о\дя \и\з стр\ат\е\г\ич\ес\к\их ц\е\л\е\й \д\еят\е\ль\н\ост\и \пр\е\д\пр\ият\ия, \r\n\п\л\а\н\ир\о\в\ать \пр\е\д\пр\и\н\и\м\ат\е\льс\кую \д\еят\е\ль\н\ость. \r\n3.3. \Осущ\ест\в\лять \к\о\нтр\о\ль \з\а р\а\зр\а\б\от\к\о\й \и р\е\а\л\и\з\ац\и\е\й \r\n\б\и\з\н\ес-\п\л\а\н\о\в \и \к\о\м\м\ерч\ес\к\их ус\л\о\в\и\й, \з\а\к\люч\а\е\мых с\о\г\л\аш\е\н\и\й, \д\о\г\о\в\ор\о\в \и \r\n\к\о\нтр\а\кт\о\в, \оц\е\н\и\в\ать ст\е\п\е\нь \в\о\з\м\о\ж\н\о\г\о р\ис\к\а. \r\n3.4. А\н\а\л\и\з\ир\о\в\ать \и р\еш\ать \ор\г\а\н\и\з\ац\и\о\н\н\о-т\ех\н\ич\ес\к\и\е, \r\nэ\к\о\н\о\м\ич\ес\к\и\е, \к\а\др\о\вы\е \и с\оц\и\а\ль\н\о-\пс\их\о\л\о\г\ич\ес\к\и\е \пр\о\б\л\е\мы \в ц\е\лях \r\nст\и\му\л\ир\о\в\а\н\ия \пр\о\и\з\в\о\дст\в\а \и у\в\е\л\ич\е\н\ия \о\бъ\е\м\а с\быт\а \пр\о\ду\кц\и\и, \r\n\п\о\выш\е\н\ия \к\ач\ест\в\а \и \к\о\н\кур\е\нт\ос\п\ос\о\б\н\ост\и т\о\в\ар\о\в \и ус\лу\г, э\к\о\н\о\м\н\о\г\о \и \r\nэфф\е\кт\и\в\н\о\г\о \ис\п\о\ль\з\о\в\а\н\ия \м\ат\ер\и\а\ль\ных, ф\и\н\а\нс\о\вых \и тру\д\о\вых р\есурс\о\в. \r\n3.5. \Осущ\ест\в\лять \п\о\д\б\ор \и р\асст\а\н\о\в\ку \к\а\др\о\в, \м\от\и\в\ац\ию \их \r\n\пр\оф\есс\и\о\н\а\ль\н\о\г\о р\а\з\в\ит\ия, \оц\е\н\ку \и ст\и\му\л\ир\о\в\а\н\и\е \к\ач\ест\в\а тру\д\а. \r\n3.6. \Ор\г\а\н\и\з\о\вы\в\ать с\вя\з\и с \д\е\л\о\вы\м\и \п\арт\н\ер\а\м\и, с\ист\е\му с\б\ор\а \r\n\н\е\о\бх\о\д\и\м\о\й \и\нф\ор\м\ац\и\и \и р\асш\ир\е\н\ия \в\н\еш\н\их с\вя\з\е\й \и \о\б\м\е\н\а \о\пыт\о\м. \r\n3.7. \Осущ\ест\в\лять \а\н\а\л\и\з с\пр\ос\а \н\а \пр\о\и\з\в\о\д\и\мую \пр\о\ду\кц\ию \и\л\и \r\nус\лу\г\и, \пр\о\г\н\о\з \и \м\от\и\в\ац\ию с\быт\а \п\оср\е\дст\в\о\м \и\зуч\е\н\ия \и \оц\е\н\к\и \r\n\п\отр\е\б\н\ост\е\й \п\о\ку\п\ат\е\л\е\й. \r\n3.8. \Уч\аст\в\о\в\ать \в р\а\зр\а\б\от\к\е \и\н\н\о\в\ац\и\о\н\н\о\й \и \и\н\в\ест\иц\и\о\н\н\о\й \r\n\д\еят\е\ль\н\ост\и, р\е\к\л\а\м\н\о\й стр\ат\е\г\и\и, с\вя\з\а\н\н\о\й с р\а\з\в\ит\и\е\м \r\n\пр\е\д\пр\и\н\и\м\ат\е\льс\к\о\й \д\еят\е\ль\н\ост\и. \r\n3.9. \О\б\ес\п\еч\и\в\ать р\ост \пр\и\бы\ль\н\ост\и, \к\о\н\кур\е\нт\ос\п\ос\о\б\н\ост\и \и \r\n\к\ач\ест\в\а т\о\в\ар\о\в \и ус\лу\г, \п\о\выш\е\н\и\е эфф\е\кт\и\в\н\ост\и тру\д\а. \r\n3.10. \Осущ\ест\в\лять \к\о\ор\д\и\н\ац\ию \д\еят\е\ль\н\ост\и \в р\а\м\к\ах \о\пр\е\д\е\л\е\н\н\о\г\о \r\n\н\а\пр\а\в\л\е\н\ия (уч\аст\к\а), \а\н\а\л\и\з \е\е эфф\е\кт\и\в\н\ост\и, \пр\и\н\и\м\ать р\еш\е\н\ия \п\о \r\n\н\а\и\б\о\л\е\е р\ац\и\о\н\а\ль\н\о\му \ис\п\о\ль\з\о\в\а\н\ию \вы\д\е\л\е\н\ных р\есурс\о\в. \r\n3.11. \Пр\и\в\л\е\к\ать \к р\еш\е\н\ию \з\а\д\ач \к\о\нсу\льт\а\нт\о\в \и э\кс\п\ерт\о\в \п\о \r\nр\а\з\л\ич\ны\м \в\о\пр\ос\а\м (\пр\а\в\о\вы\м, т\ех\н\ич\ес\к\и\м, ф\и\н\а\нс\о\вы\м \и \др.). \r\n3.12. \Осущ\ест\в\лять ру\к\о\в\о\дст\в\о \п\о\дч\и\н\е\н\ны\м\и \е\му р\а\б\от\н\и\к\а\м\и. \r\n\r\nIV. \Пр\а\в\а \r\n\r\n\М\е\н\е\д\ж\ер \и\м\е\ет \пр\а\в\о: \r\n4.1. \З\н\а\к\о\м\иться с \пр\о\е\кт\а\м\и р\еш\е\н\и\й ру\к\о\в\о\дст\в\а \пр\е\д\пр\ият\ия, \r\n\к\ас\ающ\и\м\ися \е\г\о \д\еят\е\ль\н\ост\и. \r\n4.2. \Вы\н\ос\ить \н\а р\асс\м\отр\е\н\и\е ру\к\о\в\о\дст\в\а \пр\е\д\л\о\ж\е\н\ия \п\о у\лучш\е\н\ию \r\n\д\еят\е\ль\н\ост\и \пр\е\д\пр\ият\ия \п\о \н\ах\о\дящ\и\мся \в \е\г\о \к\о\м\п\ет\е\нц\и\и \в\о\пр\ос\а\м. \r\n4.3. \П\о\д\п\исы\в\ать \и \в\и\з\ир\о\в\ать \д\о\ку\м\е\нты \в \пр\е\д\е\л\ах с\в\о\е\й \r\n\к\о\м\п\ет\е\нц\и\и. \r\n4.4. \С\о\о\бщ\ать \н\е\п\оср\е\дст\в\е\н\н\о\му ру\к\о\в\о\д\ит\е\лю \о \вс\ех \выя\в\л\е\н\ных \в \r\n\пр\оц\есс\е \ис\п\о\л\н\е\н\ия с\в\о\их \д\о\л\ж\н\ост\ных \о\бя\з\а\н\н\ост\е\й \н\е\д\ост\ат\к\ах \в \r\n\пр\е\д\пр\и\н\и\м\ат\е\льс\к\о\й \д\еят\е\ль\н\ост\и \пр\е\д\пр\ият\ия (\е\г\о стру\ктур\ных \r\n\п\о\др\а\з\д\е\л\е\н\иях) \и \в\н\ос\ить \пр\е\д\л\о\ж\е\н\ия \п\о \их устр\а\н\е\н\ию. \r\n4.5. \Вы\н\ос\ить \н\а р\асс\м\отр\е\н\и\е \д\ир\е\кт\ор\а \пр\е\д\пр\ият\ия: \r\n- \пр\е\дст\а\в\л\е\н\ия \о \н\а\з\н\ач\е\н\и\и, \п\ер\е\м\ещ\е\н\и\и \и \ос\в\о\б\о\ж\д\е\н\и\и \от \r\n\з\а\н\и\м\а\е\мых \д\о\л\ж\н\ост\е\й \п\о\дч\и\н\е\н\ных \е\му р\а\б\от\н\и\к\о\в; \r\n- \пр\е\д\л\о\ж\е\н\ия: \r\n- \о \п\о\ощр\е\н\и\и \от\л\ич\и\вш\ихся р\а\б\от\н\и\к\о\в; \r\n- \о \н\а\л\о\ж\е\н\и\и \в\зыс\к\а\н\и\й \н\а \н\аруш\ит\е\л\е\й \пр\о\и\з\в\о\дст\в\е\н\н\о\й \и тру\д\о\в\о\й \r\n\д\исц\и\п\л\и\ны. \r\n4.6. \Тр\е\б\о\в\ать \от \а\д\м\и\н\истр\ац\и\и \пр\е\д\пр\ият\ия \о\к\а\з\а\н\ия с\о\д\е\йст\в\ия \в \r\n\ис\п\о\л\н\е\н\и\и с\в\о\их \д\о\л\ж\н\ост\ных \о\бя\з\а\н\н\ост\е\й \и \пр\а\в. \r\n\r\nV. \От\в\етст\в\е\н\н\ость \r\n\r\n\М\е\н\е\д\ж\ер \н\ес\ет \от\в\етст\в\е\н\н\ость: \r\n5.1. \З\а \н\е\ис\п\о\л\н\е\н\и\е, \н\е\н\а\д\л\е\ж\ащ\е\е \ис\п\о\л\н\е\н\и\е \о\бя\з\а\н\н\ост\е\й, \r\n\пр\е\дус\м\отр\е\н\ных \н\аст\оящ\е\й \и\нстру\кц\и\е\й, \в \пр\е\д\е\л\ах, \о\пр\е\д\е\л\е\н\ных тру\д\о\вы\м \r\n\з\а\к\о\н\о\д\ат\е\льст\в\о\м \Р\осс\и\йс\к\о\й \Ф\е\д\ер\ац\и\и. \r\n5.2. \З\а с\о\в\ерш\е\н\ны\е \в \пр\оц\есс\е \осущ\ест\в\л\е\н\ия с\в\о\е\й \д\еят\е\ль\н\ост\и \r\n\пр\а\в\о\н\аруш\е\н\ия - \в \пр\е\д\е\л\ах, \о\пр\е\д\е\л\е\н\ных \а\д\м\и\н\истр\ат\и\в\ны\м, у\г\о\л\о\в\ны\м \и \r\n\гр\а\ж\д\а\нс\к\и\м \з\а\к\о\н\о\д\ат\е\льст\в\о\м \Р\осс\и\йс\к\о\й \Ф\е\д\ер\ац\и\и. \r\n5.3. \З\а \пр\ич\и\н\е\н\и\е \м\ат\ер\и\а\ль\н\о\г\о ущ\ер\б\а - \в \пр\е\д\е\л\ах, \о\пр\е\д\е\л\е\н\ных \r\nтру\д\о\вы\м \и \гр\а\ж\д\а\нс\к\и\м \з\а\к\о\н\о\д\ат\е\льст\в\о\м \Р\осс\и\йс\к\о\й \Ф\е\д\ер\ац\и\и. \r\n\r\n\r\n\Д\о\л\ж\н\ост\н\ая \и\нстру\кц\ия р\а\зр\а\б\от\а\н\а \в с\о\от\в\етст\в\и\и с ________________ \r\n(\н\а\и\м\е\н\о\в\а\н\и\е, \r\n_____________________________. \r\n\н\о\м\ер \и \д\ат\а \д\о\ку\м\е\нт\а) \r\n\r\n\r\n_________________________ \r\n\Ру\к\о\в\о\д\ит\е\ль стру\ктур\н\о\г\о \п\о\др\а\з\д\е\л\е\н\ия (\и\н\иц\и\а\лы, ф\а\м\и\л\ия) \r\n_________________________ \r\n(\п\о\д\п\ись) \r\n\r\n\" \" _____________ 20__\г.');
